import axios from "axios";
import config from './config.json';
import React from 'react';

const Recycle_bin = () => {
    // Die clearAPI-Funktion ruft die Funktionen get_players und get_games auf, um Spieler und Spiele aus der API zu löschen.
    function clearAPI() {
        get_players();
        get_games();
    }

    // Die get_players-Funktion ruft die Spielerdaten aus der API ab und ruft dann die Funktion clearPlayers auf, um die Spieler zu löschen.
    function get_players() {
        axios.get(config.preUrl + 'players/').then(response => {
            clearPlayers(response.data.players);
        });
    }

    // Die clearPlayers-Funktion nimmt die Spielerdaten als Parameter und löscht dann nacheinander jeden Spieler aus der API.
    function clearPlayers(data) {
        for (let i = 0; i < data.length; i++) {
            axios.delete(config.preUrl + 'players/' + data[i].id).then(response => {
                console.log(response.data);
            });
        }
    }

    // Die get_games-Funktion ruft Spieldaten aus der API ab und ruft dann die Funktion clearGames auf, um die Spiele zu löschen.
    function get_games() {
        axios.get(config.preUrl + 'games/').then(response => {
            clearGames(response.data.games);
        });
    }

    // Die clearGames-Funktion nimmt die Spieldaten als Parameter und löscht dann nacheinander jedes Spiel aus der API.
    function clearGames(data) {
        for (let i = 0; i < data.length; i++) {
            axios.delete(config.preUrl + 'games/' + data[i].id).then(response => {
                console.log(response.data);
            });
        }
    }

    return (
        <div>
            <button onClick={clearAPI}>API leeren</button>
        </div>
    )
}

export default Recycle_bin;
