import React,{ useRef, useState } from 'react'
import axios from 'axios'
import config from './config.json'
import { Hilfe } from './Hilfe'
import Pack from './Pack'
import PacksView from './PacksView'

function CreateGame(props) {
    let playerdata = props.pData
    const goalInRef = useRef()
    let goalPoints = 10
    let packs = [0,5,12]
    const [currentPacks,setCurrentPacks]=useState()
    const [packsView,setPacksView]= useState()

    const [showPacks, setShowPacks] = useState(false);
    const [showPacksView, setShowPacksView] = useState(false);

    let toggleShowPacks = true
    let toggleViewPacks = true

    function getPacks(packs) {
        let packsObjects = [];
      
        // Use Promise.all to wait for all Axios requests to complete
        Promise.all(packs.map(packId => axios.get(config.preUrl + 'packs/' + packId)))
          .then(responses => {
            // Iterate through the responses and push the data into packsObjects
            responses.forEach(response => {
              packsObjects.push(response.data);
            });
      
            // Call createPacks with the complete packsObjects array
            createPacks(packsObjects);
          })
          .catch(error => {
            console.error('Error fetching packs:', error);
          });
      }
    function createPacks(pArr){
        let pList = pArr.map((eintrag) => (
            <div key={eintrag.id}>
              <p> </p>
              <Pack
                packName={eintrag.name}
                packId={eintrag.id}
                bcCount={eintrag.blackCardCount}
                wcCount={eintrag.whiteCardCount}
              />
            </div>
          ));
          
          console.log(pList);
          setCurrentPacks(pList);
          
    }

    function setGoal(){
        const inGoal =goalInRef.current.value
        if (typeof inGoal === 'number' && Number.isInteger(inGoal) && inGoal > 0){
            goalPoints = inGoal
            return true
        }
        else{
            goalPoints = 10
            return false
        }
    }
    //packs einstellen
    function setPacks(){
        if (packs.length>0){
            return true
        }
        else{
            return false
        }
    }
    function handleCreateGame(playerdata,packs){
        setGoal()
        setPacks()
        createGame(playerdata, goalPoints, packs)
    

    }
    function createGame(playerdata,goal){
        var userid = playerdata.id
        //packs und goal einstellen #########

        var Jstring = '{"owner":'+userid+'}'

        var msg = JSON.parse(Jstring)
        axios.post(config.preUrl+'games/',msg).then(response => {
            
            props.changeUi(1,playerdata,response.data.id)
        })
        
    }
    function handleShowPacks(){
        if (toggleShowPacks){
            getPacks(packs)
            setShowPacks(!showPacks)
        }
        else{
            setCurrentPacks()
            setShowPacks(!showPacks)
        } 
    }
    function handleChoosePacks(){
        if(toggleViewPacks){
            setPacksView(<PacksView></PacksView>)
            setShowPacksView(!showPacksView)
        }
        else{
            setPacksView(null)
            setShowPacksView(!showPacksView)
        }
    }

  return (
    <div>
        <div>
            <label>Punkte Ziel: </label>
            <input ref={goalInRef} type="text"></input>
        </div>
        <button onClick={handleShowPacks}>Show Packs</button>
        
        <div><button onClick={handleChoosePacks}>Choose Packs</button></div>

        <button onClick={()=>handleCreateGame(props.pData)}>Start Lobby</button>
        <div>{packsView}</div>
        <div>{currentPacks}</div>
        <Hilfe/>
    </div>
  )
}

export default CreateGame
