import axios from 'axios'
import React, { useEffect, useState } from 'react'
import './Card.css'
import config from './config.json'
const Card = (props) => {
    const [text,setText] = useState("No Text Loaded")
    useEffect(()=>{
        setText(props.text)
      },[])
    function putCard(){
      let Jstring = '{"cards":['+props.cId+']}'
      let msg = JSON.parse(Jstring)
      axios.put(config.preUrl+'/games/'+props.gId+'/cards/'+props.pId,msg).then(response =>{
        console.log(response.data)
      })
    }
    function useCard(){
      props.setCardToOffer(props.cId)
    }
  return (
    <div id='card'>
        <p id='txt'>{text}</p>
        <div class="vertical-center">
            <button id='btn' onClick={useCard}>USE</button><label>ID: {props.cId}</label>
        </div>
    </div>
  )
}

export default Card
