import React,{ useRef, useState } from 'react'
import Pack from './Pack'
import axios from 'axios'
import config from './config.json'
function PacksView(props) {

    const [packs, setPacks] = useState()
    function getPacks(){
        axios.get(config.preUrl+'packs/').then(response => {
            createPacks(response.data.packs)
            console.log('packed')
        })
    }
    function addId(id){
        props.addId(id)
      }
      function removeId(id){
        props.removeId(id)
      }
    function createPacks(pArr){
        let pList = []
        pList.push(pArr.map((eintrag) => (
        <div>
            <p> </p>
            <Pack addId = {props.addId} removeId = {props.removeId} usePack = {props.usePack} packName = {eintrag.name} packId={eintrag.id} bcCount = {eintrag.blackCardCount} wcCount = {eintrag.whiteCardCount}/>
        </div>
        )))
        setPacks(pList)
    }
    
  return (
    <div>
        <div>{packs}</div>
        <button onClick={getPacks}>Load Packs</button>
    </div>
  )
}

export default PacksView
