import React,{ useRef, useState } from 'react'
import axios from 'axios'
import config from './config.json'
import { Hilfe } from './Hilfe'

function CreateGame(props) {
    let playerdata = props.pData
    const goalInRef = useRef()
    let goalPoints = 10

    function setGoal(){
        const inGoal =goalInRef.current.value
        if (typeof inGoal === 'number' && Number.isInteger(inGoal) && inGoal > 0){
            goalPoints = inGoal
            return true
        }
        else{
            return false
        }
    }
    function handleCreateGame(playerdata){
        if (setGoal()){
            createGame(playerdata, goalPoints)
        }

    }
    function createGame(playerdata,goal){
        var userid = playerdata.id
        //packs und goal einstellen #########
        var Jstring = '{"owner":'+userid+'}'
        var msg = JSON.parse(Jstring)
        axios.post(config.preUrl+'games/',msg).then(response => {
            
            props.changeUi(1,playerdata,response.data.id)
        })
        
    }
  return (
    <div>
        <div>
            <label>Punkte Ziel: </label>
            <input ref={goalInRef} type="text"></input>
        </div>
        
        <button onClick={()=>handleCreateGame(props.pData)}>Start Lobby</button>
        <Hilfe/>
    </div>
  )
}

export default CreateGame
