import React, { useState } from 'react'

export const Hilfe = () => {
    const [text,setText] = useState()
    const inhalt = 'Epsilorumequorum'
    const [pressed,setPressed] = useState(true)
    function openHelp(){
        if(pressed){
            setPressed( false )
            setText(        
                <p>
                    {inhalt}
                </p>
            )
        }
        else{ 
            setPressed( true )
            setText()
        }
        
    }
  return (
    <div>
        <button id='help' onClick={openHelp}>HELP</button>
        <div>
            {text}
        </div>
    </div>
  )
}
