import React, { useState } from 'react'
import './Hilfe.css'
export const Hilfe = () => {
    const [text,setText] = useState()
    const inhalt = '1. Es gibt einen Czar pro Runde der,  \n  die beste Karte mit VOTE bestätigt. \n 2. Mitspieler können eine Karte,  \n per USE einreichen wenn der \n Czar diese wählt bekommen sie einen Punkt \n \n HS Anhalt - Aufsicht: Toni Barth \n CREATOR: Tim Berend; Jason Labrenz '
    const [pressed,setPressed] = useState(true)
    function openHelp(){
        if(pressed){
            setPressed( false )
            setText(        
                <div style={{border: '3px solid rgb(174, 186, 218)'}}>
                    <p style={{margin:'5px'}}>{inhalt}</p>
                </div>
            )
        }
        else{ 
            setPressed( true )
            setText()
        }
        
    }
  return (
    <div>
        <button id='help' onClick={openHelp}>HELP</button>
        <div id='helpDiv'>
            {text}
        </div>
    </div>
  )
}
