import React,{ useRef } from 'react'

function PreGame() {
    const nameInRef = useRef()
    const outputLabelRef = useRef()
    const labelText = ''
    function handleSearch(e){
        const name =nameInRef.current.value
        if (name === '') outputLabelRef.current.labelText = "no name entered"; return
        labelText = name
        outputLabelRef.current.labelText = labelText
    }
  return (
    <div>
        <div>
            <label>Enter Name: </label>
            <input ref={nameInRef} type="text"></input>
            <button onClick={handleSearch}>SEARCH GAME</button>
        </div>
        <div>
            <label ref={outputLabelRef}></label>
        </div>

    </div>
  )
}

export default PreGame
