import React, { useState , useEffect} from "react";
import Players from './Players'
import './InGame.css'
import axios from "axios";
import config from './config.json'
import Card from "./Card";

const InGame = (props) => {
    let pointId = 0
    const playerData = props.pData
    const gameid = props.gId
    const [points, setPoints] = useState([])
    const [cards, setCards] = useState()
    useEffect(()=>{
        pullPoints()
        getCards()
      },[])
    //make div for each points of player
    function pullPoints (){
        axios.get(config.preUrl+'/games/'+gameid).then(response => {
            console.log("points pulled")
            createPoints(response.data.points)
        })
    }
    //map every point as paragraph
    function createPoints(pointArr){
        var pList = []
        pList.push(<p key={"p1"}> </p>)
        pList.push(pointArr.map((point) => (<div key={pointId+=1}> : {point} |</div>)))
        setPoints(pList)
    }
    //renders Cards of Player
    function createCards(cardsArr){
        console.log("cards created")
        let cList = []
        cList.push(cardsArr.map((eintrag) => (<Card text={eintrag.text} />)))
        setCards(cList)
    }
    function getCards(){
        let msg=JSON.stringify({
            "player": playerData.id
        })

        axios.get(config.preUrl+'/games/'+gameid+'/cards',msg).then(response => {
            console.log("Cards Added")
            createCards(response.data.cards)
        })
    }

  return (
    <div>
        <p align="center"> CARDS AGAINST HUMANITY:</p>
        <div id='wrapperPlayerPoints'>
            <div id='players'><Players pData={playerData} gId={gameid} /></div>
            <div id='points'>
                Points: 
                <div>{points}</div>
            </div>
            <div id="cards">
                <div>{cards}</div>
            </div>
        </div>
    </div>
  )
}

export default InGame
