import React,{ useEffect, useState } from 'react'
import Pack from './Pack'
import axios from 'axios'
import config from './config.json'

function PacksView(props) {
    
    // Deklaration der benötigten Zustände und Variablen
    const [packs, setPacks] = useState() // Zustand zur Speicherung der Pack-Komponenten
    const [back, setBack] = useState() // Zustand zur Anzeige der Zurück-Schaltfläche

    // Funktion zur Initialisierung des Komponentenstatus
    function _init (){
      getPacks()
      console.log('packed')
      if(props.changeUi != null){
          setBack(
            <button onClick={handleBack}>Zurück</button>
          )
      }
    }

    // Funktion zum Zurückkehren zum vorherigen Bildschirm
    function handleBack(){
      props.changeUi(0,props.pData,props.gId)
    }

    useEffect(() => {
      _init();
    }, []);

    // Funktion zum Abrufen der Pack-Daten von der API
    function getPacks(){
        axios.get(config.preUrl+'packs/').then(response => {
            createPacks(response.data.packs)
        })
    }

    // Funktion zum Hinzufügen einer Pack-ID zur Auswahl
    function addId(id){
        props.addId(id)
      }

    // Funktion zum Entfernen einer Pack-ID aus der Auswahl
    function removeId(id){
        props.removeId(id)
      }

    // Funktion zum Erstellen von Pack-Komponenten
    function createPacks(pArr){
        let pList = []
        pList.push(pArr.map((eintrag) => (
        <div>
            <p> </p>
            <Pack addId={addId} removeId={removeId} usePack={props.usePack} packName={eintrag.name} packId={eintrag.id} bcCount={eintrag.blackCardCount} wcCount={eintrag.whiteCardCount}/>
        </div>
        )))
        setPacks(pList)
    }
    
  return (
    <div>
        <div>{back}</div>
        <div>{packs}</div>
    </div>
  )
}

export default PacksView
