import React, { useState, useEffect } from "react";
import axios from "axios";
import config from './config.json'

const Players = (props) => {
    // Deklaration der benötigten Zustände und Variablen
    const playerData = props.pData // Spielerdaten
    const gameid = props.gId // Spiel-ID
    const [players , setPlayers] = useState() // Zustand zur Speicherung der Spielerliste

    // Effekt-Hook, der beim Komponentenladen aufgerufen wird
    useEffect(() => {
        getGameData() // Funktion zum Abrufen der Spieldaten aufrufen
    }, [])

    // Funktion zum Anzeigen der Spielerliste
    function showPlayers(gameData){
        var index = 0;
        for(let i = 0; i < gameData.length; i++){
            if (gameData[i].id == gameid){
                index = i
            }
        }
        var players = gameData[index].players
        var pList = []
        pList.push(<p key={"p1"}> </p>)
        pList.push(players.map((user) => (<div key={user.id}>|  {user.name} </div>)))
        setPlayers(pList)
    }

    // Funktion zum Abrufen der Spieldaten von der API
    function getGameData (){
        axios.get(config.preUrl+'games/').then(response => {
            console.log(response.data.games)
            showPlayers(response.data.games)
        }) 
    }
     
    // Anzeige der Spielerliste in einer HTML-Struktur
    return(
        <div>
            Spieler: 
            <div>{players}</div>
        </div>
    )
}

export default Players
