import React, { Component, useState, useEffect } from 'react'
import axios from "axios";

function LobbyGames({key, game, playerData}) {
  const [playersDiv , setPlayersDiv] = useState()
  let toggle_details = true
  function map_players(players){
    let gList = []
    gList.push(players.map((eintrag) => (
    <div>
        <p>{eintrag.name}</p>
    </div>
    )))
    setPlayersDiv(gList)
  }

  function getDetails(){
    if (toggle_details){
        map_players(game.players)
    }
    else{
        setPlayersDiv([])
    }
  }
  

  return (
    <div>
        <button id='btn' onClick={getDetails}>USE</button>
        <div>{playersDiv}</div>
    </div>
  )
}

export default LobbyGames
