import axios from 'axios'
import React, { useEffect, useState } from 'react'
import './Card.css'
import config from './config.json'
const Card = (props) => {
    const [text,setText] = useState("No Text Loaded")
    const [useBtn,setUseBtn] = useState()
    useEffect(()=>{
        setText(props.text)
        createButtonUse()
      },[])
    function putCard(){
      let Jstring = '{"cards":['+props.cId+']}'
      let msg = JSON.parse(Jstring)
      axios.put(config.preUrl+'/games/'+props.gId+'/cards/'+props.pId,msg).then(response =>{
        console.log(response.data)
      })
    }
    function useCard(){
      props.offerCard(props.cId,props.text)
    }
    function createButtonUse(){
      console.log(props.btnboo)
      if(props.btnboo){
        setUseBtn(<button id='btn' onClick={useCard}>USE</button>)
      }
    }
  return (
    <div id='card'>
        <p id='txt'>{text}</p>
        <div class="vertical-center">
            <div>{useBtn}</div>
        </div>
    </div>
  )
}

export default Card
