import React, { useState, useEffect } from 'react';
import Pack from './Pack';
import axios from 'axios';
import config from './config.json';

function Game(props) {
  const [detailsShown, setDetailsShown] = useState(false);
  const [packs, setPacks] = useState([]);
  const [player, setPlayer] = useState([]);

  const id = props.gId;
  const owner = props.owner;
  const goal = props.goal;
  const packsArr = props.packs;
  const playerArr = props.player;

  useEffect(() => {
    if (detailsShown) {
      getPacks();
      createPlayer();
    } else {
      setPacks([]);
      setPlayer([]);
    }
  }, [detailsShown]);

  async function getPacks() {
    const pacArr = [];
    for (let i = 0; i < packsArr.length; i++) {
      try {
        const response = await axios.get(config.preUrl + 'packs/' + packsArr[i]);
        pacArr.push(response.data); // Push the data from the response, not the entire response object
      } catch (error) {
        console.error(error);
      }
    }
    createPacks(pacArr);
  }

  function createPacks(pacArr) {
    setPacks(
      pacArr.map((eintrag) => (
        <div key={eintrag.id}>
          <p> </p>
          <Pack packName={eintrag.name} packId={eintrag.id} bcCount={eintrag.blackCardCount} wcCount={eintrag.whiteCardCount} />
        </div>
      ))
    );
  }

  function createPlayer() {
    setPlayer(
      playerArr.map((eintrag) => (
        <div key={eintrag.id}>
          <p> </p>
          <p>{eintrag.name}</p>
        </div>
      ))
    );
  }

  return (
    <div>
      <p>ID:</p>
      <p>{id}</p>

      <p>owner:</p>
      <p>{owner}</p>

      <p>Goal: </p>
      <p>{goal} Points</p>

      <button onClick={() => setDetailsShown(!detailsShown)}>Details</button>
      <div>{player}</div>
      <div>{packs}</div>
    </div>
  );
}

export default Game;
