import React, { useState } from 'react';
import Pack from './Pack';
import axios from 'axios';
import config from './config.json';

function Game(props) {
  const [detailsShown, setDetailsShown] = useState(true);
  const [packs, setPacks] = useState([]);
  const [player, setPlayer] = useState(null);
  const [details, setDetails] = useState(null);
  const [player_label, setPlayerLabel] = useState('');
  const [joinGameBtn, setJoinGameBtn] = useState(null);
  const [showPacks, setShowPacks] = useState(false);
  const [packDiv, setPackDiv] = useState(null);

  async function getPacks() {
    try {
      const response = await axios.get(config.preUrl + 'packs/');
      createPacks(response.data.packs);
    } catch (error) {
      console.error('Fehler beim Abrufen der Packs:', error);
    }
  }

  function createPacks(pacArr) {
    let mapPacs = [];
    for (let i = 0; i < pacArr.length; i++) {
      for (let j = 0; j < props.packs.length; j++) {
        if (pacArr[i].id === props.packs[j]) {
          mapPacs.push(pacArr[i]);
        }
      }
    }
    const pList = mapPacs.map((eintrag) => (
      <div key={eintrag.id}>
        <p> </p>
        <Pack
          packName={eintrag.name}
          packId={eintrag.id}
          bcCount={eintrag.blackCardCount}
          wcCount={eintrag.whiteCardCount}
        />
      </div>
    ));
    setPacks(pList);
  }

  function createPlayer() {
    const pList = playerArr.map((eintrag) => (
      <div key={eintrag.id}>
        <p> </p>
        <p>{eintrag.name}</p>
      </div>
    ));
    setPlayer(pList);
  }

  function handleJoinGameBtn(id, player_data) {
    props.joinGame(id, player_data);
  }

  function handleShowPacks() {
    if (!showPacks) {
      getPacks();
    }
    
    setPackDiv(showPacks ? null : packs); // Toggle packDiv based on showPacks
    setShowPacks(!showPacks); // Toggle showPacks state
    console.log(showPacks)
  }
  

  function handleDetails() {
    if (detailsShown) {
      getPacks();
      createPlayer();
      setDetailsShown(false);
      setDetails(
        <div>
          <p>ID: {id}</p>
          <p></p>
          <p>owner: {props.owner.name}</p>
          <p></p>
          <p>Goal: {props.goal} Points</p>
          <p></p>
        </div>
      );
      setPlayerLabel('Players:');
      setJoinGameBtn(
        <div>
          <button onClick={handleShowPacks}>
            {showPacks ? 'Hide Packs' : 'Show Packs'}
          </button>
          <button onClick={() => handleJoinGameBtn(id, player_data)}>
            JOIN GAME
          </button>
        </div>
      );
    } else {
      setDetailsShown(true);
      setPlayer('');
      setDetails(<div></div>);
      setPlayerLabel('');
      setJoinGameBtn('');
    }
  }

  const id = props.gId;
  const playerArr = props.player;
  const player_data = props.playerData;
  const owner = props.owner;
  const goal = props.goal;

  return (
    <div>
      <button onClick={handleDetails}>Details</button>
      <div>{details}</div>
      <div>{player_label}</div>
      <div>{player}</div>
      <div>{joinGameBtn}</div>
      {showPacks && <div>{packDiv}</div>}
    </div>
  );
}

export default Game;
