import React, { useState, useEffect } from 'react'
import Card from './Card'
import axios from 'axios'
import config from './config.json'

function Pack(props) {
    // Deklaration der benötigten Zustände und Variablen
    const id = props.packId
    const packName = props.packName
    const bcCount = props.bcCount
    const wcCount = props.wcCount
    const [packOpen, setPackOpen] = useState() // Zustand, um anzuzeigen, ob das Pack geöffnet ist
    const [whiteCards, setWhiteCards] = useState() // Zustand zur Speicherung der weißen Karten im Pack
    const [blackCards, setBlackCards] = useState() // Zustand zur Speicherung der schwarzen Karten im Pack
    const [buttonTag, setButtonTag] = useState("SHOW PACK") // Zustand für den Text auf dem Schaltflächen-Tag
    const [usePackBtn, setUsePackBtn] = useState() // Zustand für die Schaltfläche "Choose Pack"
    const [showUsePacks, setShowUsePacks] = useState(false); // Zustand, um anzuzeigen, ob das Pack ausgewählt ist

    // Funktion zur Initialisierung des Komponentenstatus
    function _init(){
        if (props.usePack){
            setUsePackBtn(
                <button onClick={handleUsePack}>
                {showUsePacks ? 'Pack entfernen' : 'Pack auswählen'}
              </button>
            )
        }
    }

    useEffect(() => {
        _init()
    }, []);

    // Funktion zum Anzeigen/Verbergen des Packs und Laden der Karten
    function showPack(){
        if (packOpen){
            setButtonTag("SHOW PACK")
            setBlackCards('')
            setWhiteCards('')
            setPackOpen(false)
        }
        else{
            setButtonTag("HIDE PACK")
            getCards()
            setPackOpen(true)
        }
    }

    // Funktion zum Abrufen der Karten im Pack von der API
    function getCards() {
        axios.get(config.preUrl+'packs/'+id).then(response=>{
            createWhiteCards(response.data.white)
            createBlackCards(response.data.black)
        })
    }

    // Funktion zum Erstellen von Komponenten für weiße Karten im Pack
    function createWhiteCards(cardsArr){
        let cList = []
        cList.push(cardsArr.map((eintrag) => (
        <div>
            <p> </p>
            <Card key={eintrag.id} type='WhiteCard' showBtn={false} cId={eintrag.id} text={eintrag.text} />
        </div>
        )))
        setWhiteCards(cList)
    }

    // Funktion zum Erstellen von Komponenten für schwarze Karten im Pack
    function createBlackCards(cardsArr){
        let cList = []
        cList.push(cardsArr.map((eintrag) => (
        <div>
            <p> </p>
            <Card key={eintrag.id} type='BlackCard' showBtn={false} cId={eintrag.id} text={eintrag.text} />
        </div>
        )))
        setBlackCards(cList)
    }

    // Funktion zum Bearbeiten der Auswahl eines Packs
    function handleUsePack(){
        setShowUsePacks(!showUsePacks)
        if(!showUsePacks){
            props.addId(id)
        }
        else{
            props.removeId(id)
        }
    }

  return (
    <div id = 'card'>
        {usePackBtn}
        <p>Pack</p>
        <p>ID:</p>
        <p>{id}</p>
        <p>Name:</p>
        <p>{packName}</p>
        <p>Black Card Count:</p>
        <p>{bcCount}</p>
        <p>White Card Count:</p>
        <p>{wcCount}</p>
        <button onClick={showPack}>{buttonTag}</button>
        <div>{whiteCards}</div>
        <div>{blackCards}</div>
    </div>
  )
}

export default Pack
