import React, { useState } from "react";
import PreGame from "./PreGame";
import JoinedGame from "./JoinedGame";
import InGame from "./InGame";

function App() {
  // uinum: 0 = preGame; 1 = joinedGame; 2 = inGame
  const [uiNum, setuiNum] = useState(1);
  const [playerData, setplayerData] = useState()
  const [gameid, setgameid] = useState()

  const changeUi = (num,data,gameid) => {
    setuiNum(num)
    setplayerData(data)
    setgameid(gameid)

  }

  if (uiNum == 0){
  return (
    <div>
        <PreGame changeUi = {changeUi} />
        <p>{playerData}</p>
    </div>

    );
  }
  else if(uiNum == 1){
    return (
      <div>
        <JoinedGame changeUi = {changeUi} pData={playerData} gId={gameid} />
      </div>
    )
  }
  else if(uiNum == 2){
    return (
      <div>
        <InGame />
      </div>
    )
  }
}

export default App;
