import React,{ useRef } from 'react'

function PreGame() {
    const nameInRef = useRef()
    const outputLabelRef = useRef()
    function handleSearch(e){
        const name =nameInRef.current.value
        if (name === '') outputLabelRef.labelText = "no name entered"; return
        outputLabelRef.current.labelText = name
    }
  return (
    <div>
        <div>
            <label>Enter Name: </label>
            <input ref={nameInRef} type="text"></input>
            <button onClick={handleSearch}>SEARCH GAME</button>
        </div>
        <div>
            <label ref={outputLabelRef}>{outputLabelRef.labelText}</label>
        </div>

    </div>
  )
}

export default PreGame
